import React, { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import CreateNote from './CreateNote';
import Note from './Note';

const App = () => {
  const [addItem, setAddItem] = useState([]);

  const addNote = (note) => {
    //alert('I am Clicked');
    setAddItem((oldData)=> {
      return [...oldData, note];
    });

    const onDelete =(id)=> {
       setAddItem((preData)=>
       preData.filter((currdata, indx) =>{
         return indx !== id;
       })
       );
    };
  return (
    <div>
      <Header/>
    <CreateNote
     passNote={addNote}/>
     {addItem.map((val,index)=> {
      return <Note 
        key={index}
        id={index}
        title={val.title}
        content={val.content}
        deleteItem = {onDelete}
      />
     })}
      
      <Footer/>
    </div>
  );
};
}
export default App;
