import React, {useState} from 'react'
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';

const CreateNote = (props) => {
  const [expand, setExpand] = useState(false);
  const [note, setNote] = useState({
    title: '',
    content: '',
  });

  const inputEvent =(event)=> {
    const {name, value} = event.target;

    setNote((preval)=> {
      return {
        ...preval, 
        [name]: value,
      };
   });
  };
  const addEvent =()=> {
     props.passNote(note);
     setNote({
      title: '',
      content: '',
    });
  };
  const expandIt = () =>{
    setExpand(true);
  };
  const btoNormal = () =>{
    setExpand(false);
  };
  return (
    <>
     <div className='main-note' onDoubleClick={btoNormal}>
        <form>
        {expand?
            <input type="text" placeholder='Title' onChange={inputEvent} name='title' value={note.title} autoComplete='off'/> :null}
            <textarea placeholder='Write a note' onChange={inputEvent} name="content" value={note.content}  cols="" rows="" onClick={expandIt}></textarea>
           { expand?
            <Button onClick={addEvent}>
              <AddIcon className='plus_sign'/>
            </Button> :null }
        </form>
     </div>
      
    </>
  );
};

export default CreateNote;
