import React, { useState } from 'react';
import ToDoList from './ToDoList';

const App = () =>{

 const [text, settext] = useState("");
 const [btn , setbtn] = useState([]);
  const write = (event) => {
    settext(event.target.value);
  };
  const btnClick = () => {
    setbtn((oldprev) =>{
      return  [...oldprev, text];
       
    });
    settext("");
    const deleteItems = (id) => {
      console.log("delete");
     
     setbtn((oldprev) =>{
      return oldprev.filter((arrElem, index) =>{
        return index !== id;
      });
  });
}
  return(
    <>
    <div className='main_div'>
      <div className='center_div'>
        <br />
        <h1>ToDo List</h1>
        <br />
        <input type="text" placeholder='Add items' onChange={write} value={text} />
        <button onClick={btnClick}> + </button>
        <ol>
          {btn.map( (textVal, index) =>{
            return <ToDoList key= {index}
            id={index}
            text = {textVal}/>
          })}
        </ol>
      </div>
    </div>

    </>
  )
};
}
export default App;
