        email: preValue.email,
        phone: preValue.phone,
        };
      }
      else if(name === "email"){
        return{
        fname: preValue.fname,
        lname: preValue.lname,
        phone: preValue.phone,
        email: value,
        
        };
      }
      else if(name === "phone"){
        return{
        fname: preValue.fname,
        lname: preValue.lname,
        email: preValue.email,
        phone:value,
        };
      }
        });
      }
      const ClickSubmit = (event) => {
      event.preventDefault();
      alert('form submited');
      }
   return(
    <>
