    <h1>Hello {fullName.fname} {fullName.lname}</h1>
    <p>{fullName.email}</p>
    <p>{fullName.phone}</p>
    <input type='text' placeholder='Enter Your Name' onChange={text} name='fName'  value={fullName.fname}
     />
