    />
     <input type='email' placeholder='Enter Your Email' onChange={text} name='email' value={fullName.email} 
     />
    <input type='number' placeholder='Enter Your Mobile no.' onChange={text} name='phone' value={fullName.phone} 
    />
   <button type='submit'>Click Me üëç</button>
    </div>
    </form>
    </>
   );
};
export default App;
